export function USMap() {
  const [hoveredState, setHoveredState] = useState<string | null>(null)
  const [selectedState, setSelectedState] = useState<string | null>(null)

  const getStateInfo = (abbr: string): StateData | undefined => {
    return statesData.find(s => s.abbr === abbr)
  }

  const getStateColor = (abbr: string): string => {
    const state = getStateInfo(abbr)
    if (!state) return "#e5e7eb"

    if (hoveredState === abbr || selectedState === abbr) {
      return state.foreclosureType === 'judicial' ? '#ea580c' : '#2563eb'
    }

    return state.foreclosureType === 'judicial' ? '#fed7aa' : '#dbeafe'
  }

  const formatNumber = (num: number): string => {
    return num.toLocaleString()
  }

  return (
    <div className="w-full max-w-5xl mx-auto">
      {/* Legend */}
      <div className="flex justify-center gap-6 mb-6">
        <div className="flex items-center gap-2">
          <div className="w-4 h-4 rounded bg-blue-100 border border-blue-300"></div>
          <span className="text-sm text-gray-600">Non-Judicial</span>
        </div>
        <div className="flex items-center gap-2">
          <div className="w-4 h-4 rounded bg-orange-100 border border-orange-300"></div>
          <span className="text-sm text-gray-600">Judicial</span>
        </div>
      </div>

      {/* Map Container */}
      <div className="relative">
        <svg
          viewBox="0 0 959 593"
          className="w-full h-auto"
          style={{ maxHeight: '450px' }}
        >
          {Object.entries(statePaths).map(([abbr, path]) => {
            const stateInfo = getStateInfo(abbr)
            const leadCount = stateleadCounts[abbr] || 0

            return (
              <g key={abbr}>
                <path
                  d={path}
                  fill={getStateColor(abbr)}
                  stroke="#9ca3af"
                  strokeWidth="1"
                  className="cursor-pointer transition-colors duration-200"
                  onMouseEnter={() => setHoveredState(abbr)}
                  onMouseLeave={() => setHoveredState(null)}
                  onClick={() => setSelectedState(selectedState === abbr ? null : abbr)}
                />
              </g>
            )
          })}
        </svg>

        {/* Tooltip */}
        {hoveredState && (
          <div className="absolute top-4 left-4 bg-white rounded-lg shadow-lg border border-gray-200 p-4 z-10 min-w-[200px]">
            <div className="flex items-center justify-between mb-2">
              <h3 className="font-semibold text-gray-900">{getStateInfo(hoveredState)?.name}</h3>
              <span className={`text-xs font-medium px-2 py-1 rounded ${
                getStateInfo(hoveredState)?.foreclosureType === 'judicial'
                  ? 'bg-orange-100 text-orange-700'
                  : 'bg-blue-100 text-blue-700'
              }`}>
                {getStateInfo(hoveredState)?.foreclosureType === 'judicial' ? 'Judicial' : 'Non-Judicial'}
              </span>
            </div>
            <div className="text-sm text-gray-600">
              <p className="flex justify-between">
                <span>Active Leads:</span>
                <span className="font-semibold text-[#1e3a5f]">{formatNumber(stateleadCounts[hoveredState] || 0)}</span>
              </p>
            </div>
          </div>
        )}
      </div>

      {/* Mobile State List */}
      <div className="mt-6 md:hidden">
        <p className="text-sm text-gray-500 text-center mb-4">Tap a state on the map to see details</p>
        {selectedState && (
          <div className="bg-gray-50 rounded-lg p-4 border border-gray-200">
            <div className="flex items-center justify-between mb-2">
              <h3 className="font-semibold text-gray-900">{getStateInfo(selectedState)?.name}</h3>
              <span className={`text-xs font-medium px-2 py-1 rounded ${
                getStateInfo(selectedState)?.foreclosureType === 'judicial'
                  ? 'bg-orange-100 text-orange-700'
                  : 'bg-blue-100 text-blue-700'
              }`}>
                {getStateInfo(selectedState)?.foreclosureType === 'judicial' ? 'Judicial' : 'Non-Judicial'}
              </span>
            </div>
            <p className="text-sm text-gray-600">
              Active Leads: <span className="font-semibold text-[#1e3a5f]">{formatNumber(stateleadCounts[selectedState] || 0)}</span>
            </p>
          </div>
        )}
      </div>

      {/* Total Stats */}
      <div className="mt-8 text-center">
        <p className="text-sm text-gray-500">
          Total Active Leads Nationwide:{' '}
          <span className="font-bold text-[#1e3a5f] text-lg">
            {formatNumber(Object.values(stateleadCounts).reduce((a, b) => a + b, 0))}
          </span>
        </p>
      </div>
    </div>
  )
}
